mapper.select:
		
		select * from board
 
		
			where board_no=#{board_no}
			
				and number=#{number}
			
		
		
		
		
			where type=#{type}
		
		
		
			
				where type in(1,2,3)
			
		
		
		order by board_no desc limit #{startrow}, #{limit}

select count(*) from board

	where number=#{number}
	
		and type in(1,2,3)
	



	
		where type in(1,2,3)
	




	where type=#{type}
